import React from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBInput
} from "mdbreact";
import {Modal} from "react-bootstrap";
import Button from '@material-ui/core/Button';
//import { Glyphicon } from 'react-bootstrap';
import { withStyles } from "@material-ui/styles";


var body;


class AddAttractions extends React.Component{

  constructor(props) {
    super(props);

    this.state =
    {
      attr_Name : "",
      city : "",
      category : "",
      description: "",
      location : "",
      from_Timings : "",
      upto_Timings : "",
      fare : "",
      rating : "",
      show: false,
      star: 0

    }

    this.handleAttractionChange = this.handleAttractionChange.bind(this);
    this.handleCityChange = this.handleCityChange.bind(this);
    this.handleCategoryChange = this.handleCategoryChange.bind(this);
    this.handleLocationChange = this.handleLocationChange.bind(this);
    this.handleFromChange = this.handleFromChange.bind(this);
    this.handleUptoChange = this.handleUptoChange.bind(this);
    this.handleFareChange = this.handleFareChange.bind(this);
    this.handleDescriptionChange = this.handleDescriptionChange.bind(this);
    this.onCreateClick = this.onCreateClick.bind(this);  
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  onCreateClick(event) {


    body = {
      attr_Name: this.state.attr_Name,
      city: this.state.city,
      category: this.state.category,
      description : this.state.description,
      location: this.state.location,
      from_Timings: this.state.from_Timings,
      upto_Timings: this.state.upto_Timings,
      fare: this.state.fare,
      rating: this.state.star,


    }
    console.log(body)
    let token = localStorage.getItem("AccessToken");

    const url = "http://localhost:9000/attractions";
    console.log(url)

    const AuthStr = 'Bearer '.concat(token);
    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Authorization', AuthStr);
          console.log("Access Token"+AuthStr);

    headers.append('Access-Control-Allow-origin',url);
    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('PUT','POST');

    fetch(url, {
       headers:headers,
       method: 'POST',
       body: JSON.stringify(body)
    })
    .then(response => response.json())
    .then(contents => {console.log(contents);
      this.setState({ show: false });
      window.location.reload(); 

 })
 .catch(()=> console.log("can't access" + url + "response. "))


  }

  handleAttractionChange (event) {
    this.setState({attr_Name: event.target.value});
  }

  handleCityChange (event) {
    this.setState({city: event.target.value});
  }

  handleCategoryChange(event) {
    this.setState({ category: event.target.value})
  }

  handleDescriptionChange(event) {
    this.setState({ description: event.target.value})
  }

  handleLocationChange(event) {
    this.setState({ location: event.target.value})
  }

  handleFromChange(event) {
    this.setState({ from_Timings: event.target.value})
  }

  handleUptoChange(event) {
    this.setState({ upto_Timings: event.target.value})
  }

  handleFareChange(event) {
    this.setState({ fare: event.target.value})
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  handleClick(starValue){    
    this.setState({star:starValue});

  } 

    getPickerValue = value => {
        console.log(value);
      };


    render(){

        return (
            <div>
              <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={this.handleShow}
                >
            Add Attraction
          </Button>
      <Modal show={this.state.show} onHide={this.handleClose}  >
      <Modal.Header closeButton style={{backgroundColor: "black", color: "white"}}>
              <Modal.Title><p className="h3 text-center mb-4"><br/><br/>CREATE ATTRACTION</p></Modal.Title>
            </Modal.Header>
        <Modal.Body>
       <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <form>

            <div className="grey-text">

            <MDBInput
                label="Attraction Name"
                icon="adn"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.attr_Name}
                onChange = {this.handleAttractionChange}
              />

<select className="browser-default custom-select"
              value = {this.state.city}
              onChange = {this.handleCityChange}>
          <option>Choose City</option>
          <option value="Hyderabad">Hyderabad</option>
          <option value="Banglore">Banglore</option>
          <option value="Mumbai">Mumbai</option>
