import React from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBModalFooter,
  MDBIcon,
  MDBInput
} from "mdbreact";
import {Button} from "react-bootstrap";


 var body;

class Register extends React.Component {


  constructor(props) {
    super(props);

    this.state =
    {
      username : "",
      email : "",
      password : "",
      confirmpassword : "",
      city : "",
      userNameError: "",
      emailError: "",
      cityError: ""


    }

    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleConfirmPasswordChange = this.handleConfirmPasswordChange.bind(this);
    this.handleUserChange = this.handleUserChange.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handleCityChange = this.handleCityChange.bind(this);
    this.onRegisterClick = this.onRegisterClick.bind(this);
    this.onLoginClick = this.onLoginClick.bind(this);

  }

  onRegisterClick(event) {


    body = {
      username: this.state.username,
      password: this.state.password,
      email: this.state.email,
      city: this.state.city,

    }
    console.log(body)
    console.log(body.password)
    console.log(body.username)
    console.log(body.email)
    console.log(body.city)
    const url = "http://localhost:9000/users";
    console.log(url)
    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Access-Control-Allow-origin',url);
    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('PUT','POST');

    fetch(url, {
       headers:headers,
       method: 'POST',
       body: JSON.stringify(body)
    })
    .then(response => response.json())
    .then(contents => {console.log(contents);
      this.props.history.push('/login')

 })
 .catch(()=> console.log("can't access" + url + " response. "))


  }

  validateForm() {
    return this.state.username.length > 0 && this.state.email.length > 0 && this.state.password.length > 5 && this.state.password==this.state.confirmpassword  && this.state.city.length > 0;
  }

  handlePasswordChange (event) {
    this.setState({password: event.target.value});
    console.log("Output:" +this.state.password)
  }

  handleConfirmPasswordChange (event) {
    this.setState({confirmpassword: event.target.value});
  }

  handleUserChange(event) {
    this.setState({ username: event.target.value})
  }

  handleEmailChange(event) {
    this.setState({ email: event.target.value})
  }

  handleCityChange(event) {
    this.setState({ city: event.target.value})
  }

    onLoginClick(){
    console.log(this.props.values)
    console.log(this.props.history)
    this.props.history.push('/login')
  }
render(){

  return (
    <div style={{backgroundImage: "url(" + "https://www.stockvault.net/photo/download/226095" + ")",
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh',
     }}>
    <MDBContainer>
      <MDBRow>
          <MDBCol md="4"></MDBCol>
        <MDBCol md="4">
                  <h1><center><MDBIcon icon="users" /> Register</center> </h1>
              <form>
                <div className="grey-text">
                <MDBInput
                label="Your name"
                icon="user"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.username}
                onChange = {this.handleUserChange}
              />
              <div style={{ fontSize: 12, color: "red" }}>
            {this.state.nameError}
          </div>
              <MDBInput
                label="Your email"
                icon="envelope"
                group
                type="email"
                validate
                error="wrong"
                success="right"
                value = {this.state.email}
                onChange = {this.handleEmailChange}
              />
              <MDBInput
                label="Your password"
                icon="lock"
                group
                type="password"
                validate
                value = {this.state.password}
                onChange = {this.handlePasswordChange}
              />
              <MDBInput
                label="Confirm your Password"
                icon="exclamation-triangle"
                group
                type="password"
                validate
                value = {this.state.confirmpassword}
                onChange = {this.handleConfirmPasswordChange}
              />
              <MDBInput
                label="Your Permanent Location"
                icon="globe"
                group
                type="text"
                validate
                error="wrong"
                success="right"
                value = {this.state.city}
                onChange = {this.handleCityChange}
              />

                </div>

                <div className="text-center mt-4">
              <Button onClick={this.onRegisterClick} disabled={! this.validateForm()} style={{background : "#FFFFCC"}}>Register</Button>
              </div>
              </form>
              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Already a member? <a onClick={this.onLoginClick} style={{background : "#FFFFCC"}}>Login</a></p>
                </div>
              </MDBModalFooter>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
    </div>
  );
};

}


export default Register;
