import React from "react";
import {
  MDBContainer,
  MDBRow,
  MDBIcon,
  MDBCol,
  MDBInput
} from "mdbreact";
import {Modal} from "react-bootstrap";
import {Button} from "react-bootstrap";
import Button1 from "@material-ui/core/Button";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";


class Schedule extends React.Component{

  constructor(props) {
    super(props);


    this.state = {
        startDate: new Date()
    }

    this.handleChange = this.handleChange.bind(this);    
    this.onContinueClick = this.onContinueClick.bind(this);  

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  onContinueClick(event) {

    let city=this.state.city
    console.log(city);
    console.log("sending city"+city)
    let path= `/cardview/`;
    this.props.history.push({
      pathname: path,
      state: {
        city: city,
        flag: 1
      }
   }) 
  }


  handleChange(date) {
    this.setState({
      startDate: date
    });
  }


  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

    render(){


        return (
            <div>
              <Button1 size="small" color="primary" style={{float :"right"}} onClick={this.handleShow}>
                        Add to Schedule
                    </Button1>
      <Modal show={this.state.show} onHide={this.handleClose}  >
      <Modal.Header closeButton style={{backgroundColor: "black", color: "white"}}>
              <Modal.Title><p className="h4 text-center mb-4">CHOOSE THE DATE</p></Modal.Title>
            </Modal.Header>
        <Modal.Body style={{width:"1000px"}}>
       <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <form>
          <DatePicker
                selected={this.state.startDate}
                onChange={this.handleChange}
            />
      <br/> <br/>
    </form>
    </MDBCol>

    </MDBRow>


    </MDBContainer>
    </Modal.Body>
    <Modal.Footer >
              <Button  onClick={this.handleClose} style={{fontSize:"15px", backgroundColor:"#16839E", width: "120px"}}>
                Close
              </Button>
              <Button onClick={this.onContinueClick} style={{fontSize:"15px", backgroundColor:"#16839E", width: "140px"}}>
              Continue
               </Button>
            </Modal.Footer>
    </Modal>
      </div>
          );
    }


}


export default Schedule;
