import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import Header from "../headers/index";
import Carousel from 'react-bootstrap/Carousel';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';

import { Map, Marker, Popup, TileLayer } from 'react-leaflet';
import L from 'leaflet';
// import "../../map/map.css";



const styles = theme => ({
  layout: {
    width: 'auto',
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(1100 + theme.spacing.unit * 3 * 2)]: {
      width: 1100,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  mainFeaturedPost: {
    backgroundColor: theme.palette.grey[800],
    color: theme.palette.common.white,
    // marginBottom: theme.spacing.unit * 4,
  },
  mainFeaturedPostContent: {
    padding: `${theme.spacing.unit * 6}px`,
    [theme.breakpoints.up('md')]: {
      paddingRight: 0,
    },
  },
  mainGrid: {
    marginTop: theme.spacing.unit * 3,
  },
  card: {
    display: 'flex',
  },
  cardDetails: {
    flex: 1,
  },
  cardMedia: {
    width: 160,
  },

  sidebarAboutBox: {
    padding: theme.spacing.unit * 2,
    backgroundColor: theme.palette.grey[200],
  },
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    flexWrap: 'nowrap',
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  },
});

var body;
const position = [17.440081, 78.348915];
class ViewDetails extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      attr_Name : "",
      city : "",
      category : "",
      description: "",
      location : "",
      from_Timings : "",
      upto_Timings : "",
      fare : "",
      rating : "",
      latitude: "",
      longitude: "",
      imgurls: [],
      id : "",
    }
  }
  componentDidMount(event) {

    let id= this.props.location.state.id;
    console.log(this.props.location.state.id);
    console.log(id);
    body = {
      aid: id,
    }
    console.log(body);
    console.log(body.aid);

      const url = "http://localhost:9000/attractions/id"; 
      let headers = new Headers();

      headers.append('Content-Type', 'application/json');
      headers.append('Accept', 'application/json');

      headers.append('Access-Control-Allow-Origin', url);
      headers.append('Access-Control-Allow-Credentials', 'true');

      headers.append('PUT','POST');

      fetch(url, {
         headers:headers,
         method: 'PUT',
         body: JSON.stringify(body)
      })
      .then(response => response.json())
      .then(contents => { 
        console.log("In fetch: "+ contents);

                          this.setState({
                            attr_Name : contents.attr_Name,
                            city : contents.city,
                            category : contents.category,
                            description: contents.description,
                            location : contents.location,
                            from_Timings : contents.from_Timings,
                            upto_Timings : contents.upto_Timings,
                            fare : contents.fare,
                            rating : contents.rating,
                            imgurls: contents.imageUrls,
                            latitude: contents.latitude,
                            longitude: contents.longitude,
                          })

                          console.log("Hello"  + this.state.imgurls)
                          console.log("Hello hi"  + this.state.category)



   })
   .catch(()=> console.log("can't access" + url + "response. "))

    }

    setMarker = ({ latitude, longitude }) => {
      this.setState({
          markers: [...this.state.markers, {
              latitude,
              longitude
          }]
      })
  }
    render() {
      const {classes} = this.props;

  return (
    <div style={{backgroundImage: "url(" + "https://newevolutiondesigns.com/images/freebies/retro-wallpaper-17.jpg" + ")",
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',}}>
    <React.Fragment>
      <CssBaseline />
      <Header history={this.props.history}/>
      <div className={classes.layout} style={{paddingBottom : "200px"}}>

        <main>
            <br></br>
            <center>
           <Typography component="h1" variant="h4" color="inherit" gutterBottom>
           <b>{this.state.attr_Name}</b></Typography>
          </center>
          {/* Main featured post */}
          <br></br>
          <div className="row">
          <div className="col md-6">

            <Carousel style={{width: "520px", height: "300px"}}>
              {this.state.imgurls.map((Image,index) => {
                          return(
