import React from 'react';
import ListItem from '@material-ui/core/ListItem';

import ams from './icons/amusement.png';
import adv from './icons/adventure.png';
import wc from './icons/workshops.png';
import cs from './icons/concert.png';
import food from './icons/food.png';
import pn from './icons/party_nightlife.png';
import hp from './icons/historicals.png';
import rs from './icons/religious.png';
import others from './icons/others.png';
import Button from '@material-ui/core/Button';
import './sidebarstyles.css';


class MainListItems extends React.Component
{
  constructor(props) {
    super(props);
    this.handleCategory = this.handleCategory.bind(this);
  }


  handleCategory(event) {
    console.log(event.currentTarget.value)
    localStorage.setItem("Category",event.currentTarget.value);
    window.location.reload(); 

  }

  render() {

    return (

      <div>

    <Button value="amusement parks" onClick = {this.handleCategory} style={{width: '200px'}}>
        <img src={ams}  className={icon}  />
        <i>Amusement <br></br>Parks</i>
    </Button>
<br/>
    <Button onClick = {this.handleCategory} value="adventures" style={{width: '200px'}}>

      <img src={adv}  className={sidestyles.icon} /> 

      <i>Adventures <br></br>
        and Sports</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="workshops" style={{width: '200px'}}>

      <img src={wc}  className={sidestyles.icon} /> 

      <i>Workshops <br></br>
        and Classes</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="concerts" style={{width: '200px'}}>

      <img src={cs}  className={sidestyles.icon} /> 

      <i>Concerts <br></br>
        and Shows</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="food" style={{width: '200px'}}>
      <img src={food}  className={sidestyles.icon} /> 
      <i>Food   </i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="parties" style={{width: '200px'}}>
      <img src={pn}  className={sidestyles.icon} /> 
      <i>Parties <br></br>
        and Nightlife</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="historic places" style={{width: '200px'}}>
      <img src={hp}  className={sidestyles.icon}  /> 
      <i>Historic Places</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="religious sites" style={{width: '200px'}}>
      <img src={rs}  className={sidestyles.icon} />
      <i>Religious Sites</i>
    </Button>
    <br/>
    <Button onClick = {this.handleCategory} value="others" style={{width: '200px'}}>
      <img src={others}  className={sidestyles.icon} /> 
      <i>Others</i>
    </Button>
    <br/>
  </div>

    )
  }



} 

export default MainListItems;
