import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import Header from "../headers/index";
import Slider from '../home/carousel';


const styles = theme => ({
  layout: {
    width: 'auto',
    marginLeft: theme.spacing.unit * 3,
    marginRight: theme.spacing.unit * 3,
    [theme.breakpoints.up(1100 + theme.spacing.unit * 3 * 2)]: {
      width: 1100,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  mainFeaturedPost: {
    backgroundColor: theme.palette.grey[800],
    color: theme.palette.common.white,
    marginBottom: theme.spacing.unit * 4,
  },
  mainFeaturedPostContent: {
    padding: `${theme.spacing.unit * 6}px`,
    [theme.breakpoints.up('md')]: {
      paddingRight: 0,
    },
  },
  mainGrid: {
    marginTop: theme.spacing.unit * 3,
  },
  card: {
    display: 'flex',
  },
  cardDetails: {
    flex: 1,
  },
  cardMedia: {
    width: 160,
  },

  sidebarAboutBox: {
    padding: theme.spacing.unit * 2,
    backgroundColor: theme.palette.grey[200],
  },
});

var body;
class Blog extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      attr_Name : "",
      city : "",
      category : "",
      description: "",
      location : "",
      from_Timings : "",
      upto_Timings : "",
      fare : "",
      rating : "",
      imgurls: "",
      id : ""
    }
  }
  componentDidMount(event) {

    let id= this.props.location.state.id;
    console.log(this.props.location.state.id);
    console.log(id);
    body = {
      aid: id,
    }
    console.log(body);
    console.log(body.aid);

      const url = "http://localhost:9000/attractions/id"; 
      let headers = new Headers();

      headers.append('Content-Type', 'application/json');
      headers.append('Accept', 'application/json');

      headers.append('Access-Control-Allow-Origin', url);
      headers.append('Access-Control-Allow-Credentials', 'true');

      headers.append('PUT','POST');

      fetch(url, {
         headers:headers,
         method: 'PUT',
         body: JSON.stringify(body)
      })
      .then(response => response.json())
      .then(contents => { 
        console.log("In fetch: "+ contents);

        console.log('state '+ this.state.data)

                          this.setState({
                            attr_Name : contents.attr_Name,
                            city : contents.city,
                            category : contents.category,
                            description: contents.description,
                            location : contents.location,
                            from_Timings : contents.from_Timings,
                            upto_Timings : contents.upto_Timings,
                            fare : contents.fare,
                            rating : contents.rating,
                            imgurls: contents.imgurls,
                          })

                          console.log('data')



   })
   .catch(()=> console.log("can't access" + url + "response. "))

    }
    render() {
      const {classes} = this.props;

  return (
    <React.Fragment>
      <CssBaseline />
      <Header history={this.props.history}/>
      <div className={classes.layout}>

        <main>
            <br></br><br></br>
            <center>
           <Typography component="h1" variant="h3" color="inherit" gutterBottom>
           <b>{this.state.attr_Name} Details</b></Typography>
          </center>
          {/* Main featured post */}
          <br></br><br></br>
          <Paper >
            <Grid container>
              <Grid item md={6}>
                <div >               
                 {/* <img src={this.state.imgurls} style={{margin : "4px" height:}}/> */}
                {/* <Slider/> */}
                </div>
              </Grid>
            </Grid>
          </Paper>
          {/* End main featured post */}

          {/* Sub featured posts */}
          <Grid container spacing={40} className={classes.cardGrid}>

              <Grid item  xs={12} md={6}>
                <Card className={classes.card}>
                  <div className={classes.cardDetails}>
                    <CardContent>
                      <Typography variant="h4">
                        <b>Address </b>
                      </Typography>  <br></br>                  
                      <Typography variant="h5">
                        {this.state.location}
                      </Typography>
                     </CardContent>
                  </div>  
                </Card>
              </Grid>
              <Grid item  xs={12} md={6}>
                <Card className={classes.card}>
                  <div className={classes.cardDetails}>
                    <CardContent>
                      <Typography variant="h4">
                        <b>Category</b>
                      </Typography>  <br></br>                  
                      <Typography variant="h5">
                        {this.state.category}
                      </Typography>
                     </CardContent>
                  </div>  
                </Card>
              </Grid>
              <Grid item  xs={12} md={6}>
                <Card className={classes.card}>
                  <div className={classes.cardDetails}>
                    <CardContent>
                      <Typography variant="h4">
                      <b>Timing</b>                        
                      </Typography>  <br></br>                  
                      <Typography variant="h5">
                      {this.state.from_Timings} <b> - </b> {this.state.upto_Timings}
                      </Typography>
                     </CardContent>
                  </div>  
                </Card>
              </Grid>
              <Grid item  xs={12} md={6}>
                <Card className={classes.card}>
                  <div className={classes.cardDetails}>
                    <CardContent>
                      <Typography variant="h4">
                        <b>Fare</b>
                      </Typography>  <br></br>                  
                      <Typography variant="h5">
                      {this.state.fare}
                      </Typography>
                     </CardContent>
                  </div>  
                </Card>
              </Grid>

          </Grid>
          {/* End sub featured posts */}
          <Grid container spacing={40} className={classes.mainGrid}>
            {/* Main content */}
            <Grid item xs={12} md={8}>
              <Typography variant="h4" gutterBottom>
               <b>Description</b> 
              </Typography>
              <Divider />
              {this.state.description}

                <br></br><br></br>
                <Typography variant="h4" gutterBottom>
                <b>Reviews</b>
              </Typography>
              <Divider />
              Its an amazing place with scenic beauty
            </Grid>
            {/* End main content */}
            {/* Sidebar */}
            <Grid item xs={12} md={4}>
              <Paper elevation={0} className={classes.sidebarAboutBox}>
                <Typography variant="h4" gutterBottom>
                  <b>Rating</b>
                </Typography>
                <Typography variant="h6" gutterBottom>
                {this.state.rating}
                </Typography>
              </Paper>


            </Grid>
            {/* End sidebar */}
          </Grid>
        </main>
      </div>

    </React.Fragment>
  );
}
}

Blog.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Blog);
